'use strict';

var objectKeys = require( '@stdlib/utils/keys' );
var ns = require( './../lib' );

var random;
var out;

// List array random number generators:
console.log( objectKeys( ns ) );

// Accessing random array generators from the `array` namespace:
out = ns.arcsine( 10, 2.0, 5.0 );
console.log( out ); // returns <Float64Array>

out = ns.weibull( 10, 2.0, 5.0 );
console.log( out ); // returns <Float64Array>

out = ns.laplace( 10, 2.0, 5.0 );
console.log( out ); // returns <Float64Array>

// Accessing random array generators factory from the `array` namespace:
random = ns.arcsine.factory();
out = random( 10, 2.0, 5.0 );
console.log( out ); // returns <Float64Array>

random = ns.cosine.factory();
out = random( 10, 2.0, 5.0 );
console.log( out ); // returns <Float64Array>

random = ns.gamma.factory();
out = random( 10, 2.0, 5.0 );
console.log( out ); // returns <Float64Array>
